INSERT INTO `t_content_txt` VALUES ('453', '463', '<section><blockquote style=\"margin: 2px 0px; padding: 2px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; border: 1px dotted rgb(250, 140, 0);\"><section><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; font-size: 14px; color: rgb(121, 121, 121); letter-spacing: 0.25px; line-height: 1.75em; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; text-indent: 32px; color: rgb(127, 127, 127); box-sizing: border-box !important; word-wrap: break-word !important;\">为贯彻全民健身国家战略，根据国务院《“健康中国2030”规划纲要》和《全民健身计划（2016-2020年）》通知的指导思想，牢固树立和贯彻落实创新、协调、绿色、开放、共享的发展理念，丰富群众文体生活，进一步推动排舞广场舞运动健康、有序、科学发展，由国家体育总局体操中心和全国排舞广场舞推广中心主办，全国排舞广场舞推广中心重庆市中心承办的“健康<span style=\"margin: 0px; padding: 0px; max-width: 100%; letter-spacing: 2px; box-sizing: border-box !important; word-wrap: break-word !important;\"></span>中国&nbsp; 一起舞吧”中国排舞公益行活动来到了三峡库区腹地——万州。</span></p></section></blockquote></section><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: \"Hiragino Sans GB\", \"Microsoft YaHei\", Arial, sans-serif; line-height: 25.6px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: \"Hiragino Sans GB\", \"Microsoft YaHei\", Arial, sans-serif; line-height: 25.6px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important; text-align: center;\"><img class=\"\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/KRBibtPM3kPfXYmMxqngT8rdYBFHsP3c4xNc0EYDiaInazG5E25XE7VZ09tpTxGCG4zvdeYDkdXmm50