DROP TABLE IF EXISTS `t_train`;
CREATE TABLE `t_train` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL COMMENT '标题',
  `content` longtext COMMENT '详情',
  `status` char(1) NOT NULL DEFAULT '1' COMMENT '发布状态',
  `province` varchar(50) NOT NULL COMMENT '所在省份',
  `gmt_begin` date DEFAULT NULL COMMENT '开始日期',
  `gmt_end` date DEFAULT NULL COMMENT '到期日期',
  `gmt_create` datetime DEFAULT NULL,
  `gmt_modified` datetime DEFAULT NULL,
  `gmt_index` datetime DEFAULT NULL COMMENT '索引时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb4 COMMENT='内容扩展属性表';

-- ----------------------------
--  Records of `t_train`
